            if ($num <= 19) $event_type = 'Знамение';
            if ($num <= 17) $event_type = 'Приметное место';
            if ($num <= 15) $event_type = 'Окружающая среда и происшествия';
            if ($num <= 12) $event_type = 'Встреча агрессивная или нейтральная';
            if ($num <= 6) $event_type = 'Монстры или животные';
